by_id_logins.map:
function(doc) {
  if(doc.type == "property") {
    if(doc.clients) {
      for(var i in doc.clients) {
        emit(doc._id, {_id: i});
      }
    }
  }
}

by_login.map:
function(doc) {
  if(doc.type == "property") {
    if(doc.clients) {
      for(var i in doc.clients) {
        emit(i, doc);
      }
    }
  }
}
